import React from 'react';

class App extends React.Component {
  state = {
    count: 0
  };

  plus = () => {
    this.state.count = 1;
    console.log(`++`);
  };
  minus = () => {
    console.log(`--`);
  };

  render() {
    return (<div>
      <h1>The number is {this.state.count}</h1>
      <button onClick={this.plus}>Plus</button>
      <button onClick={this.minus}>Minus</button>
    </div>);
  }
}

export default App;
