function Movie({ id, name, image }) {
  return <div>
    <h2>I like {name} movies!</h2>
    <img src={image} alt={id} style="width: 100px;" />
  </div>;
}

const movieILike = [
  { 
    id: 1, name: "horor", image: "https://i.ibb.co/frqPT76/In-The-Tall-Grass.jpg" 
  },
  { 
    id: 2, name: "fantasy", image: "https://i.ibb.co/xqYFSCc/Harry-Potter.jpg" 
  },
  { 
    id: 3, name: "romance", image: "https://i.ibb.co/Lr4Cfmt/LaLaLand.jpg" 
  },
  { 
    id: 4, name: "comic", image: "https://i.ibb.co/gZkCGCj/Home-Alone.png" 
  },
  { 
    id: 5, name: "thriller", image: "https://i.ibb.co/G9bFnrp/Orphan.jpg" 
  }
];

function App() {
  return (
    <div className="App">
      {movieILike.map(movie => 
        (<Movie key={movie.id} name={movie.name} image={movie.image} />
      ))}
    </div>
  );
}

export default App;
