import React, { Component } from 'react';
import { IoArrowBackCircle } from 'react-icons/io5';
import './Profile.css';

class Profile extends Component {

  constructor(props) {
    super(props);
    this.goBack = this.goBack.bind(this);
  }

  goBack() {
    this.props.history.push('/user');
  }


  render() {
    const onChange = (e) => {
      const img = e.target.files[0];
      const formData = new FormData();
      formData.append('img', img);
      console.log(formData);
      for (const keyValue of formData) console.log(keyValue);
    }

    return (
      <main className="profile">
        <button className="profile-back" onClick={ this.goBack }>
          <IoArrowBackCircle />
        </button>

        <div className="profile-title">
          나의 정보
        </div>

        <section className="profile-content">

            <div className="profile-sub-title">
              프로필
            </div>
            <div className="profile-item">
              <div className="profile-label">사진</div>
              <input type='file' 
                accept='image/jpg,image/png,image/jpeg,image/gif' 
                name='profile_img' 
                onChange={ onChange } />
            </div>
            <div className="profile-item">
              <div className="profile-label">이름</div>
              <input size="35" />
            </div>
            <div className="profile-item">이메일</div>
            <div className="profile-item">설명</div>
        </section>
      </main>
    );
  }

}

export default Profile;
