import React, { Component } from 'react';
import Form from '../components/Form';
import Palette from '../components/Palette';
import TodoItemList from '../components/TodoItemList';
import TodoTemplate from '../components/TodoTemplate';

const colors = ['#000000','#1d3557', '#6b705c', '#ff6b6b'];

class App extends Component {

  // 🤔? id의 초기값을 왜 설정해주는 걸까?
  // id = 7

  state = {

  }

  // 😯! input 태그에 입력된 값을 state의 input으로 넘긴다.
  handleChange = (e) => {
    this.setState({
      input: e.target.value
    });
  }

  /*
  😯!
    input: '' 
    -> input을 비운다.(초기화)

    그러면 빈 값인 상태로 배열에 추가되지 않을까? 
    -> 배열에 추가되는 객체에는 text: input으로 state에서 값을 불러와 넣어주고,
      비워지는 input은 실제로 사용자들이 입력하는 input 태그를 말한다.

    todos.concat(...) 
    -> 기존의 배열에 새로운 객체를 추가한 새로운 배열을 반환한다.
  */
  handleCreate = () => {
    const { input, todos, color } = this.state;
    this.setState({
      input: '',
      todos: todos.concat({
        id: todos.length,
        text: input,
        checked: false,
        color
      })
    });
  }

  // 😯! Enter 키를 눌러도 Create 버튼을 누른 것과 같이 동작한다.
  handleKeyPress = (e) => {
    if(e.key === 'Enter') {
      this.handleCreate();
    }
  }

  // 🤔? 어지럽다.
  handleToggle = (id) => {
    const { todos } = this.state;
    const index = todos.findIndex(todo => todo.id === id);
    const selected = todos[index];
    const nextTodos = [...todos];

    nextTodos[index] = {
      ...selected,
      checked: !selected.checked
    };


    this.setState({
      todos: nextTodos
    });
  }

  handleRemove = (id) => {
    const { todos } = this.state;
    this.setState({
      todos: todos.filter(todo => todo.id !== id)
    });
  }

  handleSelectColor = (color) => {
    this.setState({
      color
    })
  }

  render() {
    const { input, todos, color } = this.state;
    const {
      handleChange,
      handleCreate,
      handleKeyPress,
      handleToggle,
      handleRemove,
      handleSelectColor
    } = this;

    return (
      <TodoTemplate 
        form={(
          <Form 
            value={ input }
            onKeyPress={ handleKeyPress }
            onChange={ handleChange }
            onCreate={ handleCreate }
            color={ color } />
        )} 
        items={(
          <TodoItemList 
            todos={ todos }
            onToggle={ handleToggle }
            onRemove={ handleRemove } />
        )}
        palette={(
          <Palette colors={ colors } selected={ color } onSelect={ handleSelectColor } />
        )} />
    );
  }

}

export default App;
