import React, { Component } from 'react';
import Form from '../components/Form';
import Palette from '../components/Palette';
import TodoItemList from '../components/TodoItemList';
import TodoTemplate from '../components/TodoTemplate';

const colors = ['#1d3557', '#6d6875', '#6b705c', '#ff6b6b'];

class App extends Component {

  id = 3

  state = {
    input: '',
    todos: [
      { id: 0, text: 'hello', checked: false },
      { id: 1, text: 'world', checked: true },
      { id: 2, text: '!!', checked: false }
    ],
    color: '#343a40'
  }

  handleChange = (e) => {
    this.setState({
      // input íƒœê·¸ì— ìž…ë ¥ëœ ê°’ì„ stateì˜ inputìœ¼ë¡œ ë„˜ê¸´ë‹¤.
      input: e.target.value
    });
  }

  handleCreate = () => {
    const { input, todos, color } = this.state;
    this.setState({
      // inputì„ ë¹„ìš´ë‹¤. ðŸ¤”? ë¹ˆ ê°’ì´ ë°°ì—´ì— ì¶”ê°€ë˜ëŠ” ê±´ ì•„ë‹ê¹Œ?
      input: '',
      // ê¸°ì¡´ì˜ todos ë°°ì—´ì— ê°’ì„ ì¶”ê°€í•˜ì—¬ ìƒˆë¡œìš´ ë°°ì—´ì„ ë°˜í™˜í•œë‹¤.
      todos: todos.concat({
        id: this.id++,
        text: input,
        checked: false,
        color
      })
    });
  }

  handleKeyPress = (e) => {
    if(e.key === 'Enter') {
      // Enter í‚¤ë¥¼ ëˆŒëŸ¬ë„ Create ë²„íŠ¼ì„ ëˆ„ë¥¸ ê²ƒê³¼ ê°™ì´ ë™ìž‘í•œë‹¤.
      this.handleCreate();
    }
  }

  // ðŸ¤”? ìž˜ ëª¨ë¥´ê² ë‹¤.
  handleToggle = (id) => {
    const { todos } = this.state;
    const index = todos.findIndex(todo => todo.id === id);
    const selected = todos[index];
    const nextTodos = [...todos];

    nextTodos[index] = {
      ...selected,
      checked: !selected.checked
    };

    this.setState({
      todos: nextTodos
    });
  }

  handleRemove = (id) => {
    const { todos } = this.state;
    this.setState({
      todos: todos.filter(todo => todo.id !== id)
    });
  }

  handleSelectColor = (color) => {
    this.setState({
      color
    })
  }

  handleGoDetail = () => {(
    <Link to="" />
  )}

  render() {
    const { input, todos, color } = this.state;
    const {
      handleChange,
      handleCreate,
      handleKeyPress,
      handleToggle,
      handleRemove,
      handleSelectColor,
      handleGoDetail
    } = this;

    return (
      <TodoTemplate 
        form={(
          <Form 
            value={ input }
            onKeyPress={ handleKeyPress }
            onChange={ handleChange }
            onCreate={ handleCreate }
            color={ color } />
        )} 
        items={(
          <TodoItemList 
            todos={ todos }
            onToggle={ handleToggle }
            onRemove={ handleRemove }
            goDetail={ handleGoDetail } />
        )}
        palette={(
          <Palette colors={ colors } selected={ color } onSelect={ handleSelectColor } />
        )} />
    );
  }

}

export default App;
